INSERT INTO examples(id_stylistic_device,author,text) VALUE(1, "Victor Hugo"       , "« Je vis cette faucheuse Elle était dans son champ.Elle allait à grands pas moissonnant et fauchant,Noir squelette laissant passer le crépuscule. »"                                         );
INSERT INTO examples(id_stylistic_device,author,text) VALUE(2, "Jean Racine"       , "Ici, l'allitération en 's' imite le sifflement des serpents : « Pour qui sont ces serpents qui sifflent sur vos têtes ? »"                                         );
