INSERT INTO examples(id_stylistic_device,author,text) VALUE(26,"René Descartes"    , "« Car d’où est-ce que l’effet peut tirer sa réalité sinon de sa cause ? Et comment cette cause la lui pourrait-elle communiquer, si elle ne l’avait en elle-même ? »"                                         );
